mapper.select:
/* getChatMessages 채팅방 채팅내역 조회 */ SELECT R1.* FROM (SELECT T1.MSG_IDX, T1.MEMBER_ID, T2.MEMBER_NM, T1.CONTENT, T1.MESSAGE_TYPE, DATE_FORMAT(T1.INS_DT, '%Y-%m-%d %H:%i:%s') AS SEND_TIME, T1.BUNDLE_YN FROM ch_chat_room_msg T1, mm_member_info T2 WHERE T1.MEMBER_ID = T2.MEMBER_ID AND T1.ROOM_IDX = #{roomIdx}
AND MSG_IDX < #{startIdx}
ORDER BY MSG_IDX DESC LIMIT #{pageSize} ) R1 ORDER BY R1.MSG_IDX ASC
