mapper.select:
/* getOrganizationMember 조직의 사용자 조회 */ SELECT T1.MEMBER_ID AS memberId , T1.MEMBER_NM AS memberName , T3.ORGANIZATION_NAME AS organizationName , T2.POSITION_NAME AS positionName FROM mm_member_info T1 LEFT JOIN sy_position_info T2 ON T1.POSITION_IDX = T2.POSITION_IDX INNER JOIN sy_organization_info T3 ON T1.ORGANIZATION_IDX = T3.ORGANIZATION_IDX WHERE T1.DEL_YN != 'Y' AND T3.ORGANIZATION_IDX IN (SELECT ORGANIZATION_IDX FROM sy_organization_info WHERE USE_YN = 'Y'
AND ORGANIZATION_ID LIKE CONCAT((SELECT ORGANIZATION_ID FROM sy_organization_info WHERE ORGANIZATION_IDX = #{organizationIdx} ),'%')
AND ORGANIZATION_IDX = #{organizationIdx}
) ORDER BY T1.POSITION_IDX ASC

/*
   getPreviousDepthValue
   조직생성 시 현재까지 생성된 조직중 동레벨의 가장 큰 depth value 조회
*/
SELECT IFNULL(SUBSTRING_INDEX(REVERSE(MAX(ORGANIZATION_ID)), '-', 1) + 1, 1) AS last_part
  FROM sy_organization_info
 WHERE LEVEL = #{level}

   AND DEPTH_1 = #{depth1}


   AND DEPTH_2 = #{depth2}


   AND DEPTH_3 = #{depth3}


   AND DEPTH_4 = #{depth4}


   AND DEPTH_5 = #{depth5}
